import { Avatar } from "@material-ui/core";
import React, { useState } from "react";
import "./ChatIndividual.css";

function ChatIndividual() {
  const adminEnvio = (e) => {
    e.preventDefault();
    setMensajes([...mensajes, { mensaje: input }]);
    setInput("");
  };

  const [input, setInput] = useState("");
  const [mensajes, setMensajes] = useState([
    {
      nombre: "Brad Pitt",
      imagen:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Brad_Pitt_2019_by_Glenn_Francis.jpg/220px-Brad_Pitt_2019_by_Glenn_Francis.jpg",
      mensaje: "Estas ahí...?",
    },

    {
      nombre: "Brad Pitt",
      imagen:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Brad_Pitt_2019_by_Glenn_Francis.jpg/220px-Brad_Pitt_2019_by_Glenn_Francis.jpg",
      mensaje: "Hola que tal?",
    },

    {
      mensaje: "si dime",
    },
  ]);

  return (
    <div className="chatIndividual">
      <p className="chatIndividual__timestamp">
        CONSEGUISTE UN MATCH EL 19/1/21
      </p>
      {mensajes.map((mensaje) =>
        mensaje.nombre ? (
          <div className="chatIndividual__mensaje">
            <Avatar
              className="chatIndividual__avatar"
              alt={mensaje.nombre}
              src={mensaje.imagen}
            />

            <p className="chatIndividual__mensaje__texto">{mensaje.mensaje}</p>
          </div>
        ) : (
          <div className="chatIndividual__mensaje">
            <p className="chatIndividual__mensaje__propio">{mensaje.mensaje}</p>
          </div>
        )
      )}

      <form className="chatIndividual__input">
        <input
          className="chatIndividual__input__input"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Escribe un mensaje..."
        />
        <button
          onClick={adminEnvio}
          type="submit"
          className="chatIndividual__input__send"
        >
          Enviar
        </button>
      </form>
    </div>
  );
}
export default ChatIndividual;
