import "./App.css";
import Header from "./Header";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import CardsTinder from "./CardsTinder";
import BotonesSwipe from "./BotonesSwipe";
import ListadoChats from "./ListadoChats";
import ChatIndividual from "./ChatIndividual";

function App() {
  return (
    <div className="App">
      <Router>
        <Switch>
          <Route path="/chats/:persona">
            <Header backButton="/chats" />
            <ChatIndividual />
          </Route>

          <Route path="/chats">
            <Header backButton="/" />
            <ListadoChats />
          </Route>

          <Route path="/">
            <Header />
            <CardsTinder />
            <BotonesSwipe />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
