import React, { useState } from "react";
import { Avatar } from "@material-ui/core";

function ChatIndividual() {
  const [input, setInput] = useState("");
  const [mensaje, setMensajes] = useState([
    {
      nombre: "Brad Pitt",
      imagen:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Brad_Pitt_2019_by_Glenn_Francis.jpg/220px-Brad_Pitt_2019_by_Glenn_Francis.jpg",
      mensaje: "Estas ahí...?",
    },
    {
      nombre: "Brad Pitt",
      imagen:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Brad_Pitt_2019_by_Glenn_Francis.jpg/220px-Brad_Pitt_2019_by_Glenn_Francis.jpg",
      mensaje: "Hola que tal?",
    },
    {
      mensaje: "si dime",
    },
  ]);

  return;
  <div className="chatIndividual">
    <p className="chatIndividual__timestamp">
      CONSEGUISTE UN MATCH EL 03/04/2021
    </p>
    {mensaje.map((mensaje) =>
      mensaje.nombre ? (
        <div className="chatIndividual__mensaje">
          <Avatar
            className="chatIndividual__avatar"
            alt={mensaje.nombre}
            src={mensaje.imagen}
          />
          <p className="chatIndividual__mensaje__texto">{mensaje.mensaje}</p>
        </div>
      ) : (
        <div className="chatIndividual__mensaje">
          <p className="chatIndividual__mensaje__propio">{mensaje.mensaje}</p>
        </div>
      )
    )}
  </div>;
}

export default ChatIndividual;
