import React, { useState } from "react";
import "./CardsTinder.css";
import CardPeople from "react-tinder-card";

function CardsTinder() {
  const [persona, setPersona] = useState([
    {
      nombre: "Laura Albarracín",
      url: "https://albalau-dev.github.io/img/member/member-1.png",
    },
    {
      nombre: "Victor Inojosa",
      url: "https://avatars.githubusercontent.com/u/11879380?v=4",
    },
    {
      nombre: "Brad Pitt",
      url:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Brad_Pitt_2019_by_Glenn_Francis.jpg/220px-Brad_Pitt_2019_by_Glenn_Francis.jpg",
    },
  ]);

  return (
    <div className="cardsTinder">
      <div className="cardsTinder__contenedor"></div>
      {persona.map((persona) => (
        <CardPeople
          className="swipe"
          key={persona.nombre}
          preventSwipe={["up", "down"]}
        >
          <div
            className="tarjeta"
            style={{ backgroundImage: `url(${persona.url})` }}
          >
            <h2>{persona.nombre}</h2>
          </div>
        </CardPeople>
      ))}
    </div>
  );
}

export default CardsTinder;
